void precompute() {
    uint64_t a, b, x;
    x = s0;
    a = succ(x);
    b = succ(succ(x));
    while (a != b) {
        a = succ(a);
        b = succ(succ(b));
    }

    uint64_t first;
    uint64_t length;
    a = x;   // First overall node
    int first_id = 0;
    int cnt = 0;
    while (a != b) {
        blocks_start[cnt / block_size] += even(a);
        blocks_start_last[cnt / block_size] = a;
        cnt++;
        first_id++;
        a = succ(a);
        b = succ(b);
    }
    first = a;  // First node of cycle

    b = succ(a);
    length = 1; // Length of cycle
    period_score = even(b);

    blocks_period[0] += even(a);
    blocks_period_last[0] = a;
    cnt = 1;
    while (a != b) {
        blocks_period[cnt / block_size] += even(b);
        blocks_period_last[cnt / block_size] = b;
        b = succ(b);
        cnt++;
        period_score += even(b);
        length++;
    }
    cout << first_id << " " << first << " " << length << " " << period_score << '\n';
    start = first_id;
    period = length;
}

void print_arr(string s, uint64_t *arr, int sz) {
    cout << s << endl;
    cout << "{";
    for (int i = 0; i < sz; i++) {
        cout << arr[i] << ",}"[i == sz - 1];
    }
    cout << endl;
}

void print_block_values() {
    print_arr("blocks_start", blocks_start, max_blocks_start);
    print_arr("blocks_start_last", blocks_start_last, max_blocks_start);
    print_arr("blocks_period", blocks_period, max_blocks_period);
    print_arr("blocks_period_last", blocks_period_last, max_blocks_period);
}

void solve() {
    uint64_t n;
    cin >> n;
    uint64_t mn = min(start, n);
    uint64_t ans = 0;
    uint64_t cur = s0;

    // for (uint64_t i = 0; i < mn; i++, cur = succ(cur)) {
    //     ans += even(cur);
    // }
    for (uint64_t i = 0; i < mn / block_size; i++) {
        ans += blocks_start[i];
        cur = succ(blocks_start_last[i]);
    }
    for (uint64_t i = mn / block_size * block_size; i < mn; i++) {
        ans += even(cur);
        cur = succ(cur);
    }

    // cout << cur << endl;

    if (n > start) {
        uint64_t full = (n - start) / period;
        uint64_t rem = (n - start) % period;
        ans += full * period_score;

        // for (uint64_t i = 0; i < rem; i++, cur = succ(cur)) {
        //     ans += even(cur);
        // }
        for (uint64_t i = 0; i < rem / block_size; i++) {
            ans += blocks_period[i];
            cur = succ(blocks_period_last[i]);
        }
        for (uint64_t i = rem / block_size * block_size; i < rem; i++) {
            ans += even(cur);
            cur = succ(cur);
        }
    }
    cout << ans << '\n';
}

int main() {
    cin.tie(0)->ios::sync_with_stdio(0);
    int t = 1;
    // cin >> t;
    // precompute();
    // print_block_values();
    while (t--) {
        solve();
    }
    return 0;
}
